import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import './App.css';

const App = () => {
    const containerRef = useRef(null);

    useEffect(() => {
        const container = containerRef.current;
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, container.offsetWidth / container.offsetHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });

        renderer.setSize(container.offsetWidth, container.offsetHeight);
        renderer.setClearColor(0x000000, 0); // Set background to transparent
        container.appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xF5E8C7, 0.5);
        scene.add(ambientLight);
        const pointLight = new THREE.PointLight(0xD4A017, 1, 100);
        pointLight.position.set(5, 5, 5);
        scene.add(pointLight);

        // Load 3D Model
        const loader = new GLTFLoader();
        loader.load(
            'models/base_basic_shaded.glb', // Replace with your model file
            (gltf) => {
                const model = gltf.scene;
                scene.add(model);
                model.position.set(0, 0, 0);
                model.scale.set(1, 1, 1); // Adjust scale as needed
            },
            undefined,
            (error) => console.error('Error loading model:', error)
        );

        // Camera position
        camera.position.z = 5;

        // Orbit controls (rotate with mouse)
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        };
        animate();

        // Responsive resize
        const handleResize = () => {
            camera.aspect = container.offsetWidth / container.offsetHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.offsetWidth, container.offsetHeight);
        };
        window.addEventListener('resize', handleResize);

        // Cleanup on component unmount
        return () => {
            window.removeEventListener('resize', handleResize);
            container.removeChild(renderer.domElement);
        };
    }, []);

    return (
        <div className="App">
            <nav className="navbar">
                <div className="logo">Machina</div>
                <ul className="nav-links">
                    <li><a href="#">Home</a></li>
                    <li><a href="#">Design</a></li>
                    <li><a href="#">Performance</a></li>
                    <li><a href="#experience">Experience</a></li>
                    <li><a href="#">Contact</a></li>
                </ul>
            </nav>
            <section className="hero">
                <div className="hero-content">
                    <h1>Mach-E Machina</h1>
                    <p>Redefine Velocity</p>
                    <button className="discover-btn">Discover</button>
                </div>
                <div id="model-container" ref={containerRef} className="model-3d" style={{ width: '100%', height: '600px' }}></div>
            </section>
            <section className="big-picture">
                <img src="components/images/mache.jpg" alt="Machina Full View" className="big-image" style={{ width: '100%' }} />
            </section>
        </div>
    );
};

export default App;
