import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls';
import './App.css';

const App = () => {
    const containerRef = useRef(null);

    useEffect(() => {
        const container = containerRef.current;
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, container.offsetWidth / 600, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });

        renderer.setSize(container.offsetWidth, 600);
        container.appendChild(renderer.domElement);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xF5E8C7, 0.5);
        scene.add(ambientLight);
        const pointLight = new THREE.PointLight(0xD4A017, 1, 100);
        pointLight.position.set(5, 5, 5);
        scene.add(pointLight);

        // Load 3D Model
        const loader = new GLTFLoader();
        loader.load(
            '/models/base_basic_shaded.glb', // Replace with your model file
            (gltf) => {
                const model = gltf.scene;
                scene.add(model);
                model.position.set(0, 0, 0);
                model.scale.set(1, 1, 1); // Adjust scale as needed
            },
            undefined,
            (error) => console.error('Error loading model:', error)
        );

        // Camera position
        camera.position.z = 5;

        // Orbit controls (rotate with mouse)
        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Animation loop
        const animate = () => {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        };
        animate();

        // Responsive resize
        const handleResize = () => {
            camera.aspect = container.offsetWidth / 600;
            camera.updateProjectionMatrix();
            renderer.setSize(container.offsetWidth, 600);
        };
        window.addEventListener('resize', handleResize);

        // Cleanup on component unmount
        return () => {
            window.removeEventListener('resize', handleResize);
            container.removeChild(renderer.domElement);
        };
    }, []);

    return (
        <div className="App">
            <header className="App-header">
                <h1>Welcome to Mache Machina</h1>
                <p>This is a basic React application structure.</p>
            </header>
            <div id="model-container" ref={containerRef} style={{ width: '100%', height: '600px' }}></div>
        </div>
    );
};

export default App;
